for (let i = 0; i < ativo.length; i++) {
  if (ativo[i] == 1) {
    ativos++;
  } else if (ativo[i] == 0) {
    inativos++;
  }
}

let html = `
  <style>
    .card-container {
      display: flex;
      flex-wrap: wrap;
      gap: 16px;
      font-family: sans-serif;
    }
    .card {
      width: 240px;
      padding: 14px;
      border-radius: 10px;
      box-shadow: 0 2px 5px rgba(0,0,0,0.1);
      position: relative;
      border: 2px solid transparent;
      transition: all 0.3s ease;
    }
    .card h3 {
      margin: 0;
      font-size: 18px;
      color: #111827;
    }
  </style>

  <div class="card-container">
    <div class="card">
      <h3>ATIVOS</h3>
      <h1 style="color: green;">${ativos} / ${ativo.length}</h1>
    </div>
  </div>
`;

htmlNode.innerHTML = html;

