for (let i = 0; i < id.length; i++) {
  const temp = parseFloat(temperatura[i]);
  const ram = parseFloat(ramUsage[i]);
  const disk = parseFloat(diskUsage[i]);
  const isUp = ativo[i] == 1;
  const isCrit = critical[i] == 1;

  const tempColor = temp <= 50 ? '#2ea043' : temp <= 65 ? 'orange' : '#f85149';
  const ramColor = ram <= 40 ? '#2ea043' : ram <= 75 ? 'orange' : '#f85149';
  const diskColor = disk <= 60 ? '#2ea043' : disk <= 85 ? 'orange' : '#f85149';

  html += `
    <div class="card ${isCrit ? 'critical' : ''}">
      <div class="left">
        <h3>${nomes[i]}</h3>
