}

// Contagem
let ativos = 0, inativos = 0, critRAM = 0, critTEMP = 0;
const ativo = data.series[0].fields[0].values;
const ramUsage = data.series[0].fields[8].values;
const temperatura = data.series[0].fields[9].values;

for (let i = 0; i < ativo.length; i++) {
  if (ativo[i] == 1) ativos++;
  else inativos++;

