options.onRender:
if (!data || data.series.length < 2) {
  htmlNode.innerHTML = "<p style='color:white;'>Sem dados.</p>";
  return;
}

// RAM e DISCO com base em fields[1].labels.host
const ramMap = {};
const diskMap = {};

for (let i = 0; i < data.series.length; i++) {
  const field = data.series[i].fields[1];
  const host = field.labels?.host || `Host ${i + 1}`;
  const value = parseFloat(field.values.toArray().at(-1));

  if (data.series[i].refId === "A") {
    ramMap[host] = value;
  } else if (data.series[i].refId === "B") {
    diskMap[host] = value;
  }
}

// Gerar HTML
let html = `
  <style>
    body {
      background-color: #0d1117;
      font-family: 'Segoe UI', sans-serif;
      color: white;
    }
    .card-container {
      display: flex;
      flex-wrap: wrap;
      gap: 16px;
      padding: 10px;
    }
    .card {
      background-color: #161b22;
      border-radius: 10px;
      padding: 16px;
      width: 240px;
      box-shadow: 0 0 6px rgba(0,0,0,0.3);
    }
    .card h3 {
      font-size: 16px;
      margin-bottom: 8px;
      color: #58a6ff;
    }
    .label {
      font-size: 13px;
      margin-top: 10px;
      color: #8b949e;
    }
    .bar {
      width: 100%;
      height: 10px;
      border-radius: 5px;
      background-color: #30363d;
      overflow: hidden;
      margin-top: 4px;
    }
    .fill {
      height: 100%;
      transition: width 0.4s ease;
    }
  </style>
  <div class="card-container">
`;

// UniÃ£o dos hosts
const allHosts = new Set([...Object.keys(ramMap), ...Object.keys(diskMap)]);

for (const host of allHosts) {
  const ram = ramMap[host] ?? 0;
  const disk = diskMap[host] ?? 0;

  const ramColor = ram > 75 ? "#f85149" : ram > 50 ? "orange" : "#2ea043";
  const diskColor = disk > 85 ? "#f85149" : disk > 60 ? "orange" : "#2ea043";

  html += `
    <div class="card">
      <h3>${host}</h3>

      <div class="label">RAM: ${ram.toFixed(1)}%</div>
      <div class="bar"><div class="fill" style="width:${ram}%; background:${ramColor};"></div></div>

      <div class="label">Disco: ${disk.toFixed(1)}%</div>
      <div class="bar"><div class="fill" style="width:${disk}%; background:${diskColor};"></div></div>
    </div>
  `;
}

html += "</div>";
htmlNode.innerHTML = html;

