body {
  margin: 0;
  padding: 0;
  font-family: Arial, sans-serif;
  background-color: #f0f0f0;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
}

.adapted-container {
  display: flex;
  justify-content: space-between; /* Garante que os elementos ocupem o espaço corretamente */
  align-items: center;
  background: linear-gradient(to right, #0a7973, #edba62);
  padding: 17px;
  border-radius: 25px;
  width: 100%;
  max-width: 100%;
  height: auto;
  box-sizing: border-box;
}

.adapted-image img {
  width: 50px; /* Aumenta a largura da imagem logo1.png */
  height: auto; /* Mantém a proporção da imagem */
}


.adapted-content {
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  width: auto; /* Altera para auto para evitar que ocupe todo o espaço */
}

.adapted-text-center {
  text-align: center;
  flex: 1; /* Permite que o texto central ocupe todo o espaço restante */
  color: white;
  font-weight: bold;
  font-size: 24px; /* Aumenta o tamanho da fonte */
}


.adapted-value-container {
  display: flex;
  flex-direction: column;
  align-items: flex-end; /* Alinha o conteúdo à direita */
  width: 100%;
  position: relative; /* Necessário para o alinhamento vertical */
}

.adapted-value {
  color: #ffffff;
  font-weight: bold;
  font-size: 16px;
  margin-right: 0px; /* Ajuste o valor conforme necessário */
}

.adapted-name {
  color: #FFFFFF;
  font-weight: bold;
  font-size: 12px;
  margin-top: 5px; /* Espaçamento entre o valor e o texto */
  animation: text-glow 1.5s ease-in-out infinite alternate; /* Adiciona animação pulsando indo e voltando */
}

@keyframes text-glow {
  from {
    text-shadow: 0 0 5px #edba62, 0 0 10px #f9b234, 0 0 20px #edba62, 0 0 30px #f9b234;
  }
  to {
    text-shadow: 0 0 10px #eeeeee, 0 0 20px #edba62, 0 0 30px #56d5dc, 0 0 40px #f9b234;
  }
}

/* Consultas de mídia para dispositivos móveis */
@media (max-width: 600px) {
  .adapted-container {
    flex-direction: column;
    align-items: flex-start;
    width: 100%;
  }

  .adapted-image {
    width: 30px;
    height: 35px;
    margin-right: 0;
    margin-bottom: 10px;
  }

  .adapted-value {
    font-size: 14px;
  }

  .adapted-database {
    font-size: 8px;
  }
}

